mapper.select:


     and dj_number = #{djNumber}
     and a.create_by = #{createBy}
     and store_code = #{storeCode}
     and dj_time = #{djTime}
     and store_name like concat('%', #{storeName}, '%')
     and status = #{status}
    ${dataScope}

order by a.create_time desc

select distinct a.*,
case b.status when 0 then '未接收' when -1 then '已拒绝' when -2 then '被退回' when 1 then '已接收' when 2 then '已审核' end as dj_status_name,
(select sum(goods_num) from cg_rkd_child where dj_number=a.dj_number) as sum_num,
(select nick_name from sys_user where user_name=a.create_by) as create_name,
b.goods_price,b.goods_money,b.goods_num,b.goods_name,b.goods_dw,b.goods_address,b.person_name,b.person_code,b.id as dj_id
from cg_rkd a join cg_rkd_child b on a.dj_number=b.dj_number
join sys_user u on a.create_by=u.user_name
left join sys_dept d on u.dept_id = d.dept_id

     and a.dj_number = #{djNumber}
     and a.create_by = #{createBy}
     and store_code = #{storeCode}
     and dj_time = #{djTime}
     and store_name like concat('%', #{storeName}, '%')
     and status = #{status}
    ${dataScope}

order by a.create_time desc

select distinct a.dj_time,a.create_by,case a.status when 0 then '正操作' when 1 then '已提交' end as dj_status_name, (select sum(goods_num) from cg_rkd_child where dj_number=a.dj_number) as sum_num, (select nick_name from sys_user where user_name=a.create_by) as create_name, b.goods_num,b.goods_name,b.goods_dw,b.goods_address from cg_rkd a join cg_rkd_child b on a.dj_number=b.dj_number join sys_user u on a.create_by=u.user_name left join sys_dept d on u.dept_id = d.dept_id
${dataScope}
UNION ALL select distinct a.dj_time,a.create_by,case a.status when 0 then '正操作' when 1 then '已提交' end as dj_status_name, (select sum(goods_num) from cg_rkd_single_child where dj_number=a.dj_number) as sum_num, (select nick_name from sys_user where user_name=a.create_by) as create_name, b.goods_num,b.goods_name,b.goods_dw,b.goods_address from cg_rkd_single a join cg_rkd_single_child b on a.dj_number=b.dj_number join sys_user u on a.create_by=u.user_name left join sys_dept d on u.dept_id = d.dept_id
${dataScope}

select DISTINCT 1 as type,b.id,a.dj_number,a.create_time,b.person_code,b.person_name,a.store_code,a.store_name,a.create_by,
(select distinct nick_name from sys_user where user_name=a.create_by) as create_name,
(select count(id) from cg_rkd where create_by=a.create_by) create_count
from cg_rkd a
join  cg_rkd_child b on a.dj_number=b.dj_number left join owner_info c
on b.person_code=c.owner_code

    b.person_code LIKE '%PCM%'
     and a.dj_number = #{djNumber}
     and a.create_by = #{createBy}
     and b.person_name like concat('%', #{personName}, '%')
     and store_code = #{storeCode}
     and dj_time = #{djTime}
     and a.store_name like concat('%', #{storeName}, '%')
     and status = #{status}

UNION ALL
select DISTINCT 0 as type,a.id,a.dj_number,a.create_time,a.person_code,a.person_name,a.store_code,a.store_name,a.create_by,
(select distinct nick_name from sys_user where user_name=a.create_by) as create_name,
(select count(id) from cg_rkd where create_by=a.create_by) create_count
from cg_rkd_single a

    a.person_code LIKE '%PCM%'
     and a.dj_number = #{djNumber}
     and a.create_by = #{createBy}
     and a.person_name like concat('%', #{personName}, '%')
     and dj_time = #{djTime}
     and a.store_name like concat('%', #{storeName}, '%')
     and status = #{status}

ORDER BY create_count DESC

select
(select count(DISTINCT a.id)
from cg_rkd a
join cg_rkd_child b on a.dj_number=b.dj_number left join owner_info c
on b.person_code=c.owner_code where b.person_code=#{createBy}
and b.status=0 and a.`status`=1
)
+
(select count(DISTINCT a.id)
from cg_rkd_single a join cg_rkd_single_child b on a.dj_number=b.dj_number left join
owner_info c on a.person_code=c.owner_code where a.person_code=#{createBy}
    and a.`status`=1
)

mapper.update:
update cg_rkd
dj_number = #{djNumber}, store_code = #{storeCode}, dj_time = #{djTime}, store_name = #{storeName}, status = #{status}, is_rate = #{isRate}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, remark = #{remark}, file_name=#{fileName}, update_time = sysdate()
where id = #{id}
