mapper.select:


     and dj_number = #{djNumber}
     and kh_name like concat('%', #{khName}, '%')
     and kh_code = #{khCode}
     and whole_num = #{wholeNum}
     and whole_dw = #{wholeDw}
     and whole_price = #{wholePrice}
     and whole_bz = #{wholeBz}

order by create_time desc

select b.dj_time as remark,a.dj_number,a.kh_name,a.kh_code,a.whole_num,a.whole_dw,a.whole_price,a.whole_bz,a.whole_money,b.id ,b.goods_name,b.status,(select distinct nick_name from sys_user where user_name=a.create_by) as create_by from whole_sales_child a join sys_user u on a.create_by=u.user_name left join sys_dept d on u.dept_id = d.dept_id join whole_sales b on a.dj_number=b.dj_number
and a.dj_number = #{djNumber} ${dataScope}
order by b.dj_time desc,b.create_time desc,b.dj_number

select t.* from ( select b.dj_time as remark,a.dj_number,a.kh_name,a.whole_num,a.whole_dw,a.whole_price,a.whole_bz,a.whole_money,b.id ,b.goods_name,b.status,(select distinct nick_name from sys_user where user_name=a.create_by) as create_by from whole_sales_child a join sys_user u on a.create_by=u.user_name left join sys_dept d on u.dept_id = d.dept_id join whole_sales b on a.dj_number=b.dj_number left join owner_info c on a.create_by=c.owner_code
and a.dj_number = #{djNumber}
and c.mark_type=#{markType}
${dataScope}
UNION ALL
select b.dj_time as remark,a.dj_number,b.kh_name,a.whole_num,a.whole_dw,a.whole_price,a.whole_bz,a.whole_money,b.id ,a.goods_name,b.status,(select distinct nick_name from sys_user where user_name=a.create_by) as create_by from whole_retail_child a join sys_user u on a.create_by=u.user_name left join sys_dept d on u.dept_id = d.dept_id join whole_retail b on a.dj_number=b.dj_number left join owner_info c on a.create_by=c.owner_code
and a.dj_number = #{djNumber}
and c.mark_type=#{markType}
${dataScope}
) t order by t.remark desc

mapper.update:
update whole_sales_child
dj_number = #{djNumber}, kh_name = #{khName}, kh_code = #{khCode}, whole_num = #{wholeNum}, whole_dw = #{wholeDw}, whole_price = #{wholePrice}, whole_money = #{wholeMoney}, whole_bz = #{wholeBz}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, remark = #{remark}, update_time = sysdate()
where id = #{id}

mapper.delete:
delete from whole_sales_child where dj_number in (select dj_number from whole_sales where id in

    #{id}

)
